mapper.select:
select * from expectcourse
where 1=1

  and expectCourseId = #{expectcourseid}


  and userId = #{userid}


  and courseId = #{courseid}

select * from expectcourse
where expectCourseId = #{id}
limit 0,1

mapper.update:
update expectcourse

  
    userId = #{userid},
  
  
    courseId = #{courseid}
  

where
expectCourseId = #{expectcourseid}

mapper.delete:
delete from expectcourse
