  #{item}

)

mapper.select:
select * from expectcourse
where 1=1

  and expectCourseId = #{expectcourseid}


  and userId = #{userid}


  and courseId = #{courseid}

select count(*) from expectcourse
where 1=1

  and expectCourseId = #{expectcourseid}


  and userId = #{userid}


  and courseId = #{courseid}

  	SELECT courseid,COUNT(*) count FROM expectcourse
  	where 1=1
    
      and expectCourseId = #{expectcourseid}
    
    
      and userId = #{userid}
    
    
      and courseId = #{courseid}
    
	GROUP BY courseId
	order by count desc
